
$(document).ready(function() {

    $.ajax({
        url: "/webAjax",
        type: 'GET',
        dataType: 'json',
        beforeSend: function() {
            //$('#init').html('Buscando Noticias...');
            $('.init').show();
        }
        }).done(function(data) {
            $('.init').hide();
            listaSeplanNotices(data)
    });

    $.ajax({
        url: "/webAjax2",
        type: 'GET',
        dataType: 'json',
        beforeSend: function() {
            //$('#init').html('Buscando Noticias...');
            $('.init').show();
        }
        }).done(function(data) {
            $('.init').hide();
            listaSeplanNotices(data)
    });

    function listaSeplanNotices(data) {

        var component_seplan = '';
        for(var i = 0; i < data.length; i++) {
            // component_seplan += "<li data-target='#carouselExampleIndicators' data-slide-to='"+i+"'></li>";
        };
        $('.carousel-indicators').html(component_seplan);

        component_seplan = '';
        for(var i = 0; i < data.length; i++) {
            component_seplan += "<div class='carousel-item'>";
            // component_seplan += "<img class='d-block w-100 img-fluid' src='https://c.pxhere.com/photos/27/7b/scotland_centralscotland_benmore_stobbinnein_crianlarich_scotspine_mountains_landscape-267332.jpg!d' alt='Slide Seplan"+i+"'>";
            // component_seplan += "<div class='d-flex h-100 align-items-center justify-content-center carousel-caption d-none d-md-block bg-dark py-3'>";
            component_seplan += "<a class='text-dark' href='"+data[i][0]['titulo'][1]+"' style='text-decoration:none;'>";
            component_seplan += "<span class='data-slide float-left'>"+data[i][0]['data']+"</span><br><h3 class='font-weight-bold'>"+data[i][0]['titulo'][0]+"</h3>";
            component_seplan += "<p>"+data[i][0]['texto']+"</p>";
            component_seplan += "</a>";
            //component_seplan += "</div>";
            component_seplan += "</div>";
        };

        $('#carousel_slide').html(component_seplan);
        $('#carousel_slide .carousel-item').first().addClass('active');
        $('.carousel-indicators > li').first().addClass('active');


        // seplan_notices = JSON.parse(seplan_notices.replace(/&quot;/g,'"'))
        // seplan_notices = JSON.stringify(seplan_notices.replace(/&quot;/g,'"'));
    }

    // Init Carrousel
    $('.carousel').carousel({
        interval: 8000,
        ride: true
    });

});
