
$(document).ready(function() {

    var owl = $('.lista').owlCarousel({
        items:3,
        loop:true,
        margin:10,
        autoplay:true,
        autoplayTimeout:1000,
        autoplayHoverPause:true,
        animateIn: 'slideInUp',
        animateOut: 'fadeOut',
    });

    $.ajax({
        url: "/webAjax",
        type: 'GET',
    }).done(function(data) {
            listaSeplanNotices(data)
    });

    function listaSeplanNotices(data) {

        // seplan_notices = JSON.parse(seplan_notices.replace(/&quot;/g,'"'))
        // seplan_notices = JSON.stringify(seplan_notices.replace(/&quot;/g,'"'));

        for(var i = 0; i < data[0]['datas'].length; i++) {

            var component_seplan = 
                +      "<span class='item'>"
                +          //"<a target='_blank' href='"+data[0]['titulos'][i][1]+"' style='text-decoration:none;'>"
                +             "<span class='text-left'>"+data[0]['datas'][i]+"</span>"
                +             "<h4>"+data[0]['titulos'][i][0]+"</h4>"
                +             "<em>"+data[0]['textos'][i]+"</em>"
                +          //"</a>"
                +      "</span>";

            $('#carrousel-seplan').prepend(component_seplan);

        }
    }

});
