
$(document).ready(function() {

    $.ajax({
        url: "/webAjax3",
        type: 'GET',
        dataType: 'json',
        beforeSend: function() {
            $('.init').show();
        }
        }).done(function(dataTcema) {
            $('.init').hide();
            listaTjmaNotices(dataTcema);
    });

    function listaTjmaNotices(dataTcema) {

        var component_tcema = '';
        /*for(var i = 0; i < dataTjma.length; i++) {
            // component_seplan += "<li data-target='#carouselExampleIndicators' data-slide-to='"+i+"'></li>";
        };
        $('.carousel-indicators').html(component_tjma);*/

        for(var i = 0; i < dataTcema.length; i++) {
            component_tcema += "<div class='carousel-item'>";
            component_tcema += "<div class='d-flex h-100 align-items-center carousel-caption d-none d-md-block py-3'>";
            component_tcema += "<a class='text-dark' href='"+dataTcema[i]['link']+"' style='text-decoration:none;'>";
            // component_tcema += "<img class='d-block w-50 h-25 p-3 img-fluid float-left rounded-circle' src='"+dataTcema[i]['image']+"' alt='"+dataTcema[i]['texto']+"'>";
            component_tcema += "<span class='data-slide float-left font-weight-bolder'>"+dataTcema[i]['data']+"</span><br><h3 class='font-weight-bold'>"+dataTcema[i]['texto']+"</h3>";
            // component_tcema += "<p>"+dataTjma[i][0]['texto']+"</p>";
            component_tcema += "</a>";
            component_tcema += "</div>";
            component_tcema += "</div>";
        };

        $('#carousel_slide_tcema').html(component_tcema);
        $('#carousel_slide_tcema .carousel-item').first().addClass('active');
        $('.carousel-indicators > li').first().addClass('active');
    }

    // Init Carrousel
    $('.carousel').carousel({
        interval: 8000,
        ride: true
    });

});